'use strict';

(function() {
    angular
        .module('app', ['ngRoute', 'ui.bootstrap', 'templates', 'jsf'])
        .config(config)
        .controller('appController', appController);

    config.$inject = ['$routeProvider', '$locationProvider'];
    function config($routeProvider, $locationProvider) {
        $locationProvider.html5Mode(false);
        $routeProvider.when('/', {
            templateUrl: 'home/home.html',
            controller: 'homeController',
            controllerAs: 'vmHomeController'
        }).when('/category/:categoryId', {
            templateUrl: 'category/category.html',
            controller: 'categoryController',
            controllerAs: 'vmCategoryController'
        }).when('/product/:productId', {
            templateUrl: 'product/product.html',
            controller: 'productController',
            controllerAs: 'vmProductController'
        }).when('/cart', {
            templateUrl: 'cart/cart.html',
            controller: 'cartController',
            controllerAs: 'vmCartController'
        }).when('/account', {
            templateUrl: 'account/account.html',
            controller: 'accountController',
            controllerAs: 'vmAccountController'
        }).when('/search', {
            templateUrl: 'search/search.html',
            controller: 'searchController',
            controllerAs: 'vmSearchController'
        }).when('/search/:query', {
            templateUrl: 'search/search.html',
            controller: 'searchController',
            controllerAs: 'vmSearchController'
        }).when('/login', {
            templateUrl: 'auth/auth.html',
            controller: 'authController',
            controllerAs: 'vmAuthController'
        }).otherwise('/');
    }

    appController.$inject = ['httpService', 'cartService', '$location', 'toastService'];
    function appController (httpService, cartService, $location, toastService) {
        var vmAppController = this;

        vmAppController.isNavCollapsed = true;
        vmAppController.categoryList = [];
        vmAppController.featuredProductList = [];
        vmAppController.cart = cartService;

        vmAppController.login = login;
        vmAppController.logout = logout;
        vmAppController.search = search;
        vmAppController.subscribeNewsletter = subscribeNewsletter;

        init();

        function init () {
            httpService.get('/categoryList').then(function(response) {
                vmAppController.categoryList = response.data;
            });
        }

        function login () {
            httpService.get('/user').then(function(response) {
                vmAppController.user = response.data;
                if ($location.path() === '/login') { $location.path('/account'); }
            });
        }

        function logout () {
            vmAppController.user = null;
            if ($location.path() === '/account') { $location.path('/'); }
        }

        function search () {
            $location.path('/search/' + vmAppController.searchQuery);
        }

        function subscribeNewsletter () {
            toastService.success('Subscribed!!');
        }
    }
})();

'use strict';

(function() {
    angular
        .module('app')
        .factory('cartService', cartService);

    cartService.$inject = ['httpService', '$window'];
    function cartService(httpService, $window) {
        var cart = {itemList: []};

        init();

        function init () {
            var cartItemList = $window.localStorage.getItem('cartItemList');
            if (cartItemList) { cart.itemList = JSON.parse(cartItemList); };
        }

        function saveCart () { $window.localStorage.setItem('cartItemList', angular.toJson(cart.itemList)); }

        function getItemList () { return cart.itemList; }

        function getTotalQuantity () {
            var totalQuantity = 0;
            angular.forEach(cart.itemList, function (item) {
                totalQuantity += item.quantity;
            });
            return totalQuantity;
        }

        function getCartTotal () {
            var totalPrice = 0;
            angular.forEach(cart.itemList, function (item) {
                totalPrice += parseFloat(item.product.price) * item.quantity;
            });
            return totalPrice;
        }

        function addToCart(product, quantity) {
            var itemIndex = _.findIndex(cart.itemList, {product: {id: product.id}});
            if (itemIndex === -1) {
                itemIndex = cart.itemList.length;
                cart.itemList.push({product: product, quantity: 0});
            }
            cart.itemList[itemIndex].quantity += quantity;
            saveCart();
        }

        function removeItem (item) {
            _.remove(cart.itemList, {product: {id: item.product.id}});
            saveCart();
        }

        function updateItemQuantity (item, quantity) {
            var item = _.find(cart.itemList, {product: {id: item.product.id}});
            item.quantity = quantity;
            saveCart();
        }

        return {
            getItemList: getItemList,
            getTotalQuantity: getTotalQuantity,
            getCartTotal: getCartTotal,
            addToCart: addToCart,
            removeItem: removeItem,
            updateItemQuantity: updateItemQuantity
        };
    }
})();

